import React from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  BarElement,
  Filler,
} from 'chart.js';
import { Line, Bar } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

const StockChart = ({ stockData, company }) => {
  if (!stockData || stockData.length === 0) {
    return (
      <div className="no-selection">
        <div className="no-data-icon">ðŸ“Š</div>
        <h3>No Data Available</h3>
        <p>No stock data available for {company.symbol}</p>
      </div>
    );
  }

  // Sort data by date
  const sortedData = [...stockData].sort((a, b) => new Date(a.date) - new Date(b.date));

  const chartData = {
    labels: sortedData.map(item => new Date(item.date).toLocaleDateString()),
    datasets: [
      {
        label: 'Close Price',
        data: sortedData.map(item => item.close_price),
        borderColor: 'rgb(75, 192, 192)',
        backgroundColor: 'rgba(75, 192, 192, 0.1)',
        tension: 0.4,
        pointRadius: 4,
        pointHoverRadius: 8,
        pointBackgroundColor: 'rgb(75, 192, 192)',
        pointBorderColor: '#fff',
        pointBorderWidth: 2,
        fill: true,
        borderWidth: 3,
      },
      {
        label: 'High Price',
        data: sortedData.map(item => item.high_price),
        borderColor: 'rgb(255, 99, 132)',
        backgroundColor: 'rgba(255, 99, 132, 0.05)',
        tension: 0.4,
        pointRadius: 3,
        pointHoverRadius: 6,
        pointBackgroundColor: 'rgb(255, 99, 132)',
        pointBorderColor: '#fff',
        pointBorderWidth: 2,
        borderWidth: 2,
        borderDash: [5, 5],
      },
      {
        label: 'Low Price',
        data: sortedData.map(item => item.low_price),
        borderColor: 'rgb(54, 162, 235)',
        backgroundColor: 'rgba(54, 162, 235, 0.05)',
        tension: 0.4,
        pointRadius: 3,
        pointHoverRadius: 6,
        pointBackgroundColor: 'rgb(54, 162, 235)',
        pointBorderColor: '#fff',
        pointBorderWidth: 2,
        borderWidth: 2,
        borderDash: [5, 5],
      }
    ]
  };

  const volumeData = {
    labels: sortedData.map(item => new Date(item.date).toLocaleDateString()),
    datasets: [{
      label: 'Volume',
      data: sortedData.map(item => item.volume),
      backgroundColor: 'rgba(153, 102, 255, 0.6)',
      borderColor: 'rgb(153, 102, 255)',
      borderWidth: 1,
      borderRadius: 4,
      borderSkipped: false,
    }]
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    interaction: {
      mode: 'index',
      intersect: false,
    },
    plugins: {
      legend: {
        position: 'top',
        labels: {
          usePointStyle: true,
          padding: 20,
          font: {
            size: 12,
            weight: '600'
          }
        }
      },
      title: {
        display: true,
        text: `${company.symbol} Stock Price Chart`,
        font: {
          size: 16,
          weight: '700'
        },
        padding: {
          top: 10,
          bottom: 20
        }
      },
      tooltip: {
        mode: 'index',
        intersect: false,
        backgroundColor: 'rgba(0, 0, 0, 0.8)',
        titleColor: '#fff',
        bodyColor: '#fff',
        borderColor: 'rgba(255, 255, 255, 0.2)',
        borderWidth: 1,
        cornerRadius: 8,
        padding: 12,
        displayColors: true,
        callbacks: {
          label: function(context) {
            let label = context.dataset.label || '';
            if (label) {
              label += ': ';
            }
            if (context.parsed.y !== null) {
              if (label.includes('Volume')) {
                label += new Intl.NumberFormat().format(context.parsed.y);
              } else {
                label += new Intl.NumberFormat('en-US', {
                  style: 'currency',
                  currency: 'USD',
                  minimumFractionDigits: 2
                }).format(context.parsed.y);
              }
            }
            return label;
          },
          title: function(context) {
            return `Date: ${context[0].label}`;
          }
        }
      }
    },
    scales: {
      x: {
        display: true,
        title: {
          display: true,
          text: 'Date',
          font: {
            size: 12,
            weight: '600'
          }
        },
        grid: {
          color: 'rgba(0, 0, 0, 0.05)',
          drawBorder: false
        }
      },
      y: {
        display: true,
        title: {
          display: true,
          text: 'Price ($)',
          font: {
            size: 12,
            weight: '600'
          }
        },
        grid: {
          color: 'rgba(0, 0, 0, 0.05)',
          drawBorder: false
        },
        ticks: {
          callback: function(value) {
            return '$' + value.toFixed(2);
          }
        }
      }
    },
    elements: {
      point: {
        hoverRadius: 8,
        hoverBorderWidth: 3,
      }
    }
  };

  const volumeOptions = {
    ...options,
    plugins: {
      ...options.plugins,
      title: {
        display: true,
        text: `${company.symbol} Trading Volume`,
        font: {
          size: 16,
          weight: '700'
        },
        padding: {
          top: 10,
          bottom: 20
        }
      },
      legend: {
        display: false
      }
    },
    scales: {
      ...options.scales,
      y: {
        display: true,
        title: {
          display: true,
          text: 'Volume',
          font: {
            size: 12,
            weight: '600'
          }
        },
        grid: {
          color: 'rgba(0, 0, 0, 0.05)',
          drawBorder: false
        },
        ticks: {
          callback: function(value) {
            if (value >= 1000000) {
              return (value / 1000000).toFixed(1) + 'M';
            } else if (value >= 1000) {
              return (value / 1000).toFixed(1) + 'K';
            }
            return value;
          }
        }
      }
    }
  };

  return (
    <div style={{ height: '100%', display: 'flex', flexDirection: 'column', gap: '25px' }}>
      <div style={{ flex: '2', minHeight: '350px' }}>
        <Line data={chartData} options={options} />
      </div>
      <div style={{ flex: '1', minHeight: '180px' }}>
        <Bar data={volumeData} options={volumeOptions} />
      </div>
    </div>
  );
};

export default StockChart;
