import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CompanyList from './components/CompanyList';
import StockChart from './components/StockChart';
import StockInfo from './components/StockInfo';
import './App.css';

function App() {
  const [companies, setCompanies] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [stockData, setStockData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [isRefreshing, setIsRefreshing] = useState(false);

  // Fetch companies on component mount
  useEffect(() => {
    fetchCompanies();
  }, []);

  // Fetch stock data when company is selected
  useEffect(() => {
    if (selectedCompany) {
      fetchStockData(selectedCompany.symbol);
    }
  }, [selectedCompany]);

  const fetchCompanies = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/companies');
      setCompanies(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch companies');
      console.error('Error fetching companies:', err);
    } finally {
      setLoading(false);
    }
  };

  const fetchStockData = async (symbol) => {
    try {
      setLoading(true);
      const response = await axios.get(`/api/stocks/${symbol}?days=30`);
      setStockData(response.data);
      setError(null);
      setLastUpdated(new Date());
    } catch (err) {
      setError('Failed to fetch stock data');
      console.error('Error fetching stock data:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleCompanySelect = (company) => {
    setSelectedCompany(company);
  };

  const handleRefresh = async () => {
    if (selectedCompany && !isRefreshing) {
      setIsRefreshing(true);
      await fetchStockData(selectedCompany.symbol);
      setIsRefreshing(false);
    }
  };

  const formatLastUpdated = () => {
    if (!lastUpdated) return '';
    const now = new Date();
    const diff = now - lastUpdated;
    const minutes = Math.floor(diff / 60000);
    
    if (minutes < 1) return 'Just now';
    if (minutes === 1) return '1 minute ago';
    if (minutes < 60) return `${minutes} minutes ago`;
    
    const hours = Math.floor(minutes / 60);
    if (hours === 1) return '1 hour ago';
    if (hours < 24) return `${hours} hours ago`;
    
    return lastUpdated.toLocaleDateString();
  };

  return (
    <div className="container">
      <div className="dashboard">
        <div className="sidebar">
          <h2>📈 Stock Dashboard</h2>
          <CompanyList
            companies={companies}
            selectedCompany={selectedCompany}
            onCompanySelect={handleCompanySelect}
            loading={loading}
          />
        </div>
        
        <div className="main-content">
          <div className="chart-container">
            {selectedCompany ? (
              <>
                <div className="chart-header">
                  <h1 className="chart-title">{selectedCompany.name}</h1>
                  <p className="chart-subtitle">{selectedCompany.symbol} • {selectedCompany.sector}</p>
                  {lastUpdated && (
                    <div className="last-updated">
                      <span>Last updated: {formatLastUpdated()}</span>
                      <button 
                        className={`refresh-btn ${isRefreshing ? 'spinning' : ''}`}
                        onClick={handleRefresh}
                        disabled={isRefreshing}
                        title="Refresh data"
                      >
                        🔄
                      </button>
                    </div>
                  )}
                </div>
                
                <StockInfo stockData={stockData} />
                
                <div className="chart-wrapper">
                  {loading ? (
                    <div className="loading">
                      <div className="loading-spinner"></div>
                      <p>Loading stock data...</p>
                      <div className="loading-dots">
                        <span></span>
                        <span></span>
                        <span></span>
                      </div>
                    </div>
                  ) : error ? (
                    <div className="error">
                      <div className="error-icon">⚠️</div>
                      <h3>Oops! Something went wrong</h3>
                      <p>{error}</p>
                      <button className="retry-btn" onClick={handleRefresh}>
                        Try Again
                      </button>
                    </div>
                  ) : (
                    <StockChart stockData={stockData} company={selectedCompany} />
                  )}
                </div>
              </>
            ) : (
              <div className="no-selection">
                <div className="welcome-icon">🚀</div>
                <h2>Welcome to Stock Market Dashboard</h2>
                <p>Select a company from the sidebar to view its stock data and analytics</p>
                <div className="features-list">
                  <div className="feature-item">
                    <div className="feature-icon">📊</div>
                    <div className="feature-text">
                      <h4>Interactive Charts</h4>
                      <p>Real-time price and volume visualization</p>
                    </div>
                  </div>
                  <div className="feature-item">
                    <div className="feature-icon">📈</div>
                    <div className="feature-text">
                      <h4>Advanced Analytics</h4>
                      <p>Comprehensive stock metrics and insights</p>
                    </div>
                  </div>
                  <div className="feature-item">
                    <div className="feature-icon">📱</div>
                    <div className="feature-text">
                      <h4>Responsive Design</h4>
                      <p>Perfect experience on all devices</p>
                    </div>
                  </div>
                  <div className="feature-item">
                    <div className="feature-icon">🎨</div>
                    <div className="feature-text">
                      <h4>Modern UI</h4>
                      <p>Beautiful, intuitive interface</p>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
